mapper.select:
SELECT a.* ,b.ccgNameKo FROM cc a JOIN ccg b on b.ccgseq=a.ccg_seq WHERE 1=1
AND ccdelNy = #{shdelNy}
AND ccSeq = #{shValue} AND ccnameko LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO cc(
	ccnameko
	,ccnameeg
	,ccuseNy
	,ccdelNy
	,ccorder
)
VALUES(
	#{ccnameko}
	,#{ccnameeg}
	,#{ccuseNy}
	,#{ccdelNy}
	,#{ccorder}
)

	SELECT last_insert_id()
