mapper.select:
SELECT a.* ,(SELECT ccgNameKo FROM ccg aa WHERE 1=1 AND aa.ccgseq = a.ccg_seq)as ccgNameKo FROM cc a WHERE 1=1
AND ccdelNy = #{shdelNy}
AND ccseq = #{shValue} AND ccnameko LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO cc(
	ccnameko
	,ccnameeg
	,ccuseNy
	,ccdelNy
	,ccorder
	,ccgNameKo
)
VALUES(
	#{ccnameko}
	,#{ccnameeg}
	,#{ccuseNy}
	,#{ccdelNy}
	,#{ccorder}
	,#{ccgNameKo}
)

	SELECT last_insert_id()
