mapper.sql:
FROM cc a
WHERE 1=1
	AND ccdelNy = #{shdelNy}
	
		AND ccseq = #{shValue}
		AND ccnameko LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO cc(
	ccnameko
	,ccnameeg
	,ccuseNy
	,ccdelNy
	,ccorder
	,ccg_seq
	
)
VALUES(
	#{ccnameko}
	,#{ccnameeg}
	,#{ccuseNy}
	,#{ccdelNy}
	,#{ccorder}
	,#{ccg_seq}
	
)

	SELECT last_insert_id()

mapper.update:
UPDATE cc
SET
	ccnameko = #{ccnameko}
	,ccnameeg = #{ccnameeg}
	,ccuseNy = #{ccuseNy}
	,ccorder = #{ccorder}
	,ccdelNy = #{ccdelNy}
WHERE 
	ccseq = #{ccseq}
