mapper.select:
select a.* ,(select count(ccg_seq) From cc where 1=1 and ccg_seq=ccgseq) as ccg_seq from ccg a WHERE 1=1
AND ccgdelNy = #{shdelNy}
AND ccgSeq = #{shValue} AND ccgNameKo LIKE CONCAT('%',#{shValue},'%') AND ccgNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
	INSERT INTO ccg(
		ccgNameKo
		,ccgNameEg
		,ccguseNy
		,ccgdelNy
		,ccgorder
	)
	VALUES(
		#{ccgNameKo}
		,#{ccgNameEg}
		,#{ccguseNy}
		,#{ccgdelNy}
		,#{ccgorder}
	)
	
		SELECT last_insert_id()
