mapper.sql:
	FROM game a
	WHERE 1=1
	AND gameDelNy = #{shdelNy}
 	
		AND gameSeq = #{shValue}
		AND date LIKE CONCAT('%',#{shValue},'%')
		AND ground LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO game(
	whoHome
	,whoAway
	,date
	,time
	,ground
	,gameDelNy
)
VALUES(
	#{whoHome}
	,#{whoAway}
	,#{date}
	,#{time}
	,#{ground}
	,1
)

	SELECT last_insert_id()

mapper.update:
UPDATE game
SET
	whoHome = #{whoHome}
	,whoAway = #{whoAway}
	,date = #{date}
	,time = #{time}
	,ground = #{ground}
WHERE
	gameSeq = #{gameSeq}
