mapper.sql:
	FROM member a
	WHERE 1=1
	AND delNy = #{shdelNy}
 	
		AND seq = #{shValue}
		AND name LIKE CONCAT('%',#{shValue},'%')
		AND id LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
	INSERT INTO member(
		name
		,id
		,pwd
		,gender
		,email
		,personalAgree
		,address
		,address2
		,addressCode
		,delNy
	)
	VALUES(
		#{name}
		,#{id}
		,#{pwd}
		,#{gender}
		,#{email}
		,#{personalAgree}
		,#{address}
		,#{address2}
		,#{addressCode}
		,#{delNy}
	)
	
		SELECT last_insert_id()

mapper.update:
UPDATE member
SET
	name = #{name}
	,id = #{id}
	,pwd = #{pwd}
	,email = #{email}
	,address = #{address}
	,address2 = #{address2}
	,addressCode = #{addressCode}
	,gender = #{gender}
	,delNy = #{delNy}
	,personalAgree = #{personalAgree}
		
WHERE
	 seq = #{seq}
